-- ----------------------------
--  Table structure for `Content`
-- ----------------------------
DROP TABLE IF EXISTS `Content`;
CREATE TABLE `Content` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title_az` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Başlıq',
  `title_en` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Heading',
  `title_ru` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Заголовок',
  `content_az` text COLLATE utf8_unicode_ci NOT NULL COMMENT 'Mətn',
  `content_en` text COLLATE utf8_unicode_ci COMMENT 'Text',
  `content_ru` text COLLATE utf8_unicode_ci COMMENT 'Текст',
  `catID` int(11) unsigned DEFAULT NULL COMMENT 'Kateqoriya',
  `albumID` int(10) unsigned DEFAULT NULL COMMENT 'Fotoalbom',
  `isBlog` tinyint(1) DEFAULT '1',
  `imgUrl` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Şəkil',
  PRIMARY KEY (`id`),
  KEY `FK_BlogCatID` (`catID`),
  KEY `FK_BlogAlbumID` (`albumID`),
  CONSTRAINT `Content_ibfk_1` FOREIGN KEY (`albumID`) REFERENCES `Photoalbum` (`id`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `Content_ibfk_2` FOREIGN KEY (`catID`) REFERENCES `Hierarchy` (`id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ROW_FORMAT=COMPACT;

