-- ----------------------------
--  Table structure for `Video`
-- ----------------------------
DROP TABLE IF EXISTS `Video`;
CREATE TABLE `Video` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `url` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Link',
  `videoID` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `title_az` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Videonun adı',
  `title_ru` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Название',
  `title_en` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Title',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ROW_FORMAT=COMPACT;

